     1→// AUTOMATICALLY GENERATED SERVICE
     2→import { APP_IDS } from '@/types/app';
     3→import type { Mitarbeiter, Lagerorte, Werkzeuge, Werkzeugausgabe, Werkzeugrueckgabe } from '@/types/app';
     4→
     5→// Base Configuration
     6→const API_BASE_URL = 'https://my.living-apps.de/rest';
     7→
     8→// --- HELPER FUNCTIONS ---
     9→export function extractRecordId(url: string | null | undefined): string | null {
    10→  if (!url) return null;
    11→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    12→  const match = url.match(/([a-f0-9]{24})$/i);
    13→  return match ? match[1] : null;
    14→}
    15→
    16→export function createRecordUrl(appId: string, recordId: string): string {
    17→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    18→}
    19→
    20→async function callApi(method: string, endpoint: string, data?: any) {
    21→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    22→    method,
    23→    headers: { 'Content-Type': 'application/json' },
    24→    credentials: 'include',  // Nutze Session Cookies für Auth
    25→    body: data ? JSON.stringify(data) : undefined
    26→  });
    27→  if (!response.ok) throw new Error(await response.text());
    28→  // DELETE returns often empty body or simple status
    29→  if (method === 'DELETE') return true;
    30→  return response.json();
    31→}
    32→
    33→export class LivingAppsService {
    34→  // --- MITARBEITER ---
    35→  static async getMitarbeiter(): Promise<Mitarbeiter[]> {
    36→    const data = await callApi('GET', `/apps/${APP_IDS.MITARBEITER}/records`);
    37→    return Object.entries(data).map(([id, rec]: [string, any]) => ({
    38→      record_id: id, ...rec
    39→    }));
    40→  }
    41→  static async getMitarbeiterEntry(id: string): Promise<Mitarbeiter | undefined> {
    42→    const data = await callApi('GET', `/apps/${APP_IDS.MITARBEITER}/records/${id}`);
    43→    return { record_id: data.id, ...data };
    44→  }
    45→  static async createMitarbeiterEntry(fields: Mitarbeiter['fields']) {
    46→    return callApi('POST', `/apps/${APP_IDS.MITARBEITER}/records`, { fields });
    47→  }
    48→  static async updateMitarbeiterEntry(id: string, fields: Partial<Mitarbeiter['fields']>) {
    49→    return callApi('PATCH', `/apps/${APP_IDS.MITARBEITER}/records/${id}`, { fields });
    50→  }
    51→  static async deleteMitarbeiterEntry(id: string) {
    52→    return callApi('DELETE', `/apps/${APP_IDS.MITARBEITER}/records/${id}`);
    53→  }
    54→
    55→  // --- LAGERORTE ---
    56→  static async getLagerorte(): Promise<Lagerorte[]> {
    57→    const data = await callApi('GET', `/apps/${APP_IDS.LAGERORTE}/records`);
    58→    return Object.entries(data).map(([id, rec]: [string, any]) => ({
    59→      record_id: id, ...rec
    60→    }));
