  --accent: hsl(32 90% 95%);
  --accent-foreground: hsl(32 95% 30%);
  --destructive: hsl(0 70% 50%);
  --border: hsl(30 10% 88%);
  --input: hsl(30 10% 88%);
  --ring: hsl(32 95% 44%);
  --chart-1: hsl(32 95% 44%);
  --chart-2: hsl(142 70% 35%);
