  --chart-4: hsl(0 70% 50%);
  --chart-5: hsl(32 90% 70%);
  --sidebar: hsl(30 10% 96%);
  --sidebar-foreground: hsl(30 5% 15%);
  --sidebar-primary: hsl(32 95% 44%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(32 90% 95%);
  --sidebar-accent-foreground: hsl(32 95% 30%);
  --sidebar-border: hsl(30 10% 88%);
  --sidebar-ring: hsl(32 95% 44%);
  --success: hsl(142 70% 35%);
  --warning: hsl(32 95% 44%);
  --danger: hsl(0 70% 50%);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Space Grotesk', sans-serif;
  }
}

