          </div>
          <div className="grid grid-cols-2 gap-3">
            <div className="space-y-2">
              <Label htmlFor="wz-hersteller">Hersteller</Label>
              <Input id="wz-hersteller" value={form.hersteller} onChange={e => setForm(f => ({ ...f, hersteller: e.target.value }))} />
