                    <h3 className="font-semibold">{l.fields.ortsbezeichnung || '–'}</h3>
                    <Badge variant="secondary" className="text-xs mt-1">{LAGERORT_TYP_LABELS[l.fields.typ || ''] || '–'}</Badge>
                  </div>
