            </div>
          </div>
          <div className="space-y-2">
            <Label htmlFor="ag-zweck">Verwendungszweck</Label>
            <Input id="ag-zweck" value={form.verwendungszweck} onChange={e => setForm(f => ({ ...f, verwendungszweck: e.target.value }))} />
